import { useState } from "react";

import LoginCard from "../Cards/LoginCard";
import SignUpCard from "../Cards/SignUpCard";
import "./Login.css";



const Login = () => {

    const [isLogin, setLogin] = useState(0);

    const toggleCard = () =>{
        setLogin(!isLogin);
    }

    return (
        <>
        
        <div className="bg">
            {isLogin ? (
                <LoginCard/>
            ):(
                <SignUpCard/>
                )
            }
            
        </div>
        </>
    )
}

export default Login;
