import { useState } from "react";

import LoginCard from "../Cards/LoginCard";
import SignUpCard from "../Cards/SignUpCard";
import "./Login.css";

const Login = () => {

    const [isLogin, setLogin] = useState(0);
    const [isSignup, setSignup] = useState(0);

    const toggleCard = (val) =>{
        setLogin(val);
    }
    const toggleSignup =(val) =>{
        setSignup(val)
    }

    return (
        <div className="bg">
            <button onClick={toggleCard}> Toggle</button>
            {/* <div className="name">
                <h2 className="">Welcome to tripSathi</h2>
            </div> */}
            
            {isLogin ? (
                <LoginCard/>
            ):(
                <SignUpCard signup={toggleCard}/>
                )
            }
            
        </div>
    )
}

export default Login;
